(1, 'Contact us', '<div class="contact-infos">\r\n<div class="border-one">\r\n<h2 class="shop-heading">{global:formtitle}</h2>\r\n</div>\r\n{error}\r\n	<div class="rsform-block rsform-block-headquarters">\r\n		<div class="formControlLabel">{Headquarters:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Headquarters:body}<span class="formValidation">{Headquarters:validation}</span></div>\r\n		<p class="formDescription">{Headquarters:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-whetetofind">\r\n		<div class="formControlLabel">{WheteToFind:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{WheteToFind:body}<span class="formValidation">{WheteToFind:validation}</span></div>\r\n		<p class="formDescription">{WheteToFind:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-visitus">\r\n		<div class="formControlLabel">{VisitUs:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{VisitUs:body}<span class="formValidation">{VisitUs:validation}</span></div>\r\n		<p class="formDescription">{VisitUs:description}</p>\r\n		</div>\r\n	</div>\r\n</div>\r\n<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\r\n<fieldset class="formHorizontal formContainer enquiry-form" id="rsform_1_page_0">\r\n<div class="row">\r\n<div class="col-sm-12">\r\n	<div class="rsform-block rsform-block-enquiryform">\r\n		<div class="formControlLabel">{EnquiryForm:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{EnquiryForm:body}<span class="formValidation">{EnquiryForm:validation}</span></div>\r\n		<p class="formDescription">{EnquiryForm:description}</p>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class="col-sm-5 col-xs-12">\r\n	<div class="rsform-block rsform-block-designation">\r\n		<div class="formControlLabel">{Designation:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Designation:body}<span class="formValidation">{Designation:validation}</span></div>\r\n		<p class="formDescription">{Designation:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-name">\r\n		<div class="formControlLabel">{Name:caption}<strong class="formRequired">(*)</strong></div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Name:body}<span class="formValidation">{Name:validation}</span></div>\r\n		<p class="formDescription">{Name:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-companyname">\r\n		<div class="formControlLabel">{CompanyName:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{CompanyName:body}<span class="formValidation">{CompanyName:validation}</span></div>\r\n		<p class="formDescription">{CompanyName:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-department">\r\n		<div class="formControlLabel">{Department:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Department:body}<span class="formValidation">{Department:validation}</span></div>\r\n		<p class="formDescription">{Department:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-email">\r\n		<div class="formControlLabel">{Email:caption}<strong class="formRequired">(*)</strong></div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Email:body}<span class="formValidation">{Email:validation}</span></div>\r\n		<p class="formDescription">{Email:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-directline">\r\n		<div class="formControlLabel">{DirectLine:caption}<strong class="formRequired">(*)</strong></div>\r\n		<div class="formControls">\r\n		<div class="formBody">{DirectLine:body}<span class="formValidation">{DirectLine:validation}</span></div>\r\n		<p class="formDescription">{DirectLine:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-preferredproduct">\r\n		<div class="formControlLabel">{PreferredProduct:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{PreferredProduct:body}<span class="formValidation">{PreferredProduct:validation}</span></div>\r\n		<p class="formDescription">{PreferredProduct:description}</p>\r\n		</div>\r\n	</div>\r\n</div><!--col-->\r\n<div class="col-sm-7 col-xs-12">\r\n	<div class="rsform-block rsform-block-customisation">\r\n		<div class="formControlLabel customisation-label">{Customisation:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Customisation:body}<span class="formValidation">{Customisation:validation}</span></div>\r\n		<p class="formDescription">{Customisation:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-attachment">\r\n		<div class="formControlLabel">{Attachment:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Attachment:body}<span class="formValidation">{Attachment:validation}</span></div>\r\n		<p class="formDescription">{Attachment:description}</p>\r\n		</div>\r\n	</div>\r\n</div><!--col-->\r\n<div class="col-sm-12 col-xs-12">\r\n	<div class="rsform-block rsform-block-requirements">\r\n		<div class="formControlLabel">{Requirements:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Requirements:body}<span class="formValidation">{Requirements:validation}</span></div>\r\n		<p class="formDescription">{Requirements:description}</p>\r\n		</div>\r\n	</div>\r\n	<div class="rsform-block rsform-block-submit">\r\n		<div class="formControlLabel">{Submit:caption}</div>\r\n		<div class="formControls">\r\n		<div class="formBody">{Submit:body}<span class="formValidation">{Submit:validation}</span></div>\r\n		<p class="formDescription">{Submit:description}</p>\r\n		</div>\r\n	</div>\r\n</div>\r\n</div><!--row-->\r\n</fieldset>\r\n', 'responsive', 0, '', '<script>\r\njQuery(function($) {\r\n$(''.rsform-upload-box'').wrap(''<div class="custom-file-upload"></div>'');\r\n		  // Browser supports HTML5 multiple file?\r\n		  var multipleSupport = typeof $(''<input/>'')[0].multiple !== ''undefined'',\r\n		      isIE = /msie/i.test( navigator.userAgent );\r\n\r\n		  $.fn.customFile = function() {\r\n\r\n		    return this.each(function() {\r\n\r\n		      var $file = $(this).addClass(''custom-file-upload-hidden''), // the original file input\r\n		          $wrap = $(''<div class="file-upload-wrapper">''),\r\n		          $input = $(''<input type="text" class="file-upload-input" placeholder="Attachment" />''),\r\n		          // Button that will be used in non-IE browsers\r\n		          $button = $(''<button type="button" class="file-upload-button button-2">Browse</button>''),\r\n		          // Hack for IE\r\n		          $label = $(''<label class="file-upload-button" for="''+ $file[0].id +''">Browse</label>'');\r\n\r\n		      // Hide by shifting to the left so we\r\n		      // can still trigger events\r\n		      $file.css({\r\n		        position: ''absolute'',\r\n		        left: ''-9999px''\r\n		      });\r\n\r\n		      $wrap.insertAfter( $file )\r\n		        .append( $file, $input, ( isIE ? $label : $button ) );\r\n\r\n		      // Prevent focus\r\n		      $file.attr(''tabIndex'', -1);\r\n		      $button.attr(''tabIndex'', -1);\r\n\r\n		      $button.click(function () {\r\n		        $file.focus().click(); // Open dialog\r\n		      });\r\n\r\n		      $file.change(function() {\r\n\r\n		        var files = [], fileArr, filename;\r\n\r\n		        // If multiple is supported then extract\r\n		        // all filenames from the file array\r\n		        if ( multipleSupport ) {\r\n		          fileArr = $file[0].files;\r\n		          for ( var i = 0, len = fileArr.length; i < len; i++ ) {\r\n		            files.push( fileArr[i].name );\r\n		          }\r\n		          filename = files.join('', '');\r\n\r\n		        // If not supported then just take the value\r\n		        // and remove the path to just show the filename\r\n		        } else {\r\n		          filename = $file.val().split(''\\\\'').pop();\r\n		        }\r\n\r\n		        $input.val( filename ) // Set the value\r\n		          .attr(''title'', filename) // Show filename in title tootlip\r\n		          .focus(); // Regain focus\r\n\r\n		      });\r\n\r\n		      $input.on({\r\n		        blur: function() { $file.trigger(''blur''); },\r\n		        keydown: function( e ) {\r\n		          if ( e.which === 13 ) { // Enter\r\n		            if ( !isIE ) { $file.trigger(''click''); }\r\n		          } else if ( e.which === 8 || e.which === 46 ) { // Backspace & Del\r\n		            // On some browsers the value is read-only\r\n		            // with this trick we remove the old input and add\r\n		            // a clean clone with all the original events attached\r\n		            $file.replaceWith( $file = $file.clone( true ) );\r\n		            $file.trigger(''change'');\r\n		            $input.val('''');\r\n		          } else if ( e.which === 9 ){ // TAB\r\n		            return;\r\n		          } else { // All other keys\r\n		            return false;\r\n		          }\r\n		        }\r\n		      });\r\n\r\n		    });\r\n\r\n		  };\r\n\r\n		  // Old browser fallback\r\n		  if ( !multipleSupport ) {\r\n		    $( document ).on(''change'', ''input.customfile'', function() {\r\n\r\n		      var $this = $(this),\r\n		          // Create a unique ID so we\r\n		          // can attach the label to the input\r\n		          uniqId = ''customfile_''+ (new Date()).getTime(),\r\n		          $wrap = $this.parent(),\r\n\r\n		          // Filter empty input\r\n		          $inputs = $wrap.siblings().find(''.file-upload-input'')\r\n		            .filter(function(){ return !this.value }),\r\n\r\n		          $file = $(''<input type="file" id="''+ uniqId +''" name="''+ $this.attr(''name'') +''"/>'');\r\n\r\n		      // 1ms timeout so it runs after all other events\r\n		      // that modify the value have triggered\r\n		      setTimeout(function() {\r\n		        // Add a new input\r\n		        if ( $this.val() ) {\r\n		          // Check for empty fields to prevent\r\n		          // creating new inputs when changing files\r\n		          if ( !$inputs.length ) {\r\n		            $wrap.after( $file );\r\n		            $file.customFile();\r\n		          }\r\n		        // Remove and reorganize inputs\r\n		        } else {\r\n		          $inputs.parent().remove();\r\n		          // Move the input so it''s always last on the list\r\n		          $wrap.appendTo( $wrap.parent() );\r\n		          $wrap.find(''input'').focus();\r\n		        }\r\n		      }, 1);\r\n\r\n		    });\r\n		  }\r\n$(''input[type=file]'').customFile();\r\n\r\n});\r\n</script>', 'Contact us', 1, 1, 'en-GB', '', 1, '<p>Dear {FullName:value},</p><p> thank you for your submission. One of our staff members will contact you by  {ContactBy:value} as soon as possible.</p>', 1, '<p>Dear {FullName:value},</p><p> we received your contact request. Someone will get back to you by {ContactBy:value} soon. </p>', '{Email:value}', '', '', 'your@email.com', '', 'Your Company', 'Contact confirmation', 1, 0, '', '<p>Customize this e-mail also. You will receive it as administrator. </p><p>{FullName:caption}:{FullName:value}<br />\n{Email:caption}:{Email:value}<br />\n{CompanySize:caption}:{CompanySize:value}<br />\n{Position:caption}:{Position:value}<br />\n{ContactBy:caption}:{ContactBy:value}<br />\n{ContactWhen:caption}:{ContactWhen:value}</p>', 'youradminemail@email.com', '', '', '{Email:value}', '', 'Your Company', 'Contact', 1, '', '', '$product = JRequest::getVar(''product'');\r\nif(!empty($product)){\r\necho "<script>jQuery(function($){ $(''#PreferredProduct'').val(''".$product."'') });</script>";\r\n}', '', '', '', 0, '', '', '(*)', '', ', ', 1, '', 'userForm', '', '', '', 0, '', 1, 0, 0),
